<script>
  (function() {
    'use strict';

    function initFriendlinkCard() {
      const copyButtons = document.querySelectorAll('.friendlink-copy-btn');
      const notification = document.createElement('div');
      notification.className = 'friendlink-copy-notification';
      document.body.appendChild(notification);

      copyButtons.forEach(button => {
        button.addEventListener('click', function() {
          const textToCopy = this.getAttribute('data-clipboard-text');
          copyToClipboard(textToCopy, this);
        });
      });

      function copyToClipboard(text, button) {
        const originalText = button.textContent;

        if (navigator.clipboard && window.isSecureContext) {
          navigator.clipboard.writeText(text).then(() => {
            showCopyFeedback(button, originalText);
            showNotification('已复制到剪贴板');
          }).catch(err => {
            console.error('复制失败: ', err);
            fallbackCopyText(text, button, originalText);
          });
        } else {
          fallbackCopyText(text, button, originalText);
        }
      }

      function fallbackCopyText(text, button, originalText) {
        const textArea = document.createElement('textarea');
        textArea.value = text;
        textArea.style.position = 'fixed';
        textArea.style.opacity = '0';
        textArea.style.left = '-999999px';
        textArea.style.top = '-999999px';
        document.body.appendChild(textArea);

        try {
          textArea.select();
          textArea.setSelectionRange(0, 99999);
          const successful = document.execCommand('copy');

          if (successful) {
            showCopyFeedback(button, originalText);
            showNotification('已复制到剪贴板');
          } else {
            showNotification('复制失败，请手动复制');
          }
        } catch (err) {
          console.error('复制失败: ', err);
          showNotification('复制失败，请手动复制');
        } finally {
          document.body.removeChild(textArea);
        }
      }

      function showCopyFeedback(button, originalText) {
        button.classList.add('copied');
        button.textContent = '已复制';

        setTimeout(() => {
          button.classList.remove('copied');
          button.textContent = originalText;
        }, 2000);
      }

      function showNotification(message) {
        notification.textContent = message;
        notification.classList.add('show');

        setTimeout(() => {
          notification.classList.remove('show');
        }, 3000);
      }
    }

    if (document.readyState === 'loading') {
      document.addEventListener('DOMContentLoaded', initFriendlinkCard);
    } else {
      initFriendlinkCard();
    }
  })();
</script>
{% endraw %}

