    <img src="https://cftcr2.20010501.xyz/PicHoro/P20251112-100611.webp" 
         class="live-photo-static" 
         alt="Live Photo" 
         data-photo-src="https://cftcr2.20010501.xyz/PicHoro/P20251112-100611.webp">

    <!-- 动态视频 -->
    <video class="live-photo-video" 
           preload="metadata" 
           muted
           data-video-src="https://cftcr2.20010501.xyz/PicHoro/1757173192196.mp4">
        <source src="https://cftcr2.20010501.xyz/PicHoro/P20251112-100611-1.mp4" type="video/mp4">
    </video>

    <!-- Live 标志 -->
    <div class="live-badge">LIVE</div>

    <!-- 静音切换按钮 -->
    <button class="live-photo-mute-btn" aria-label="切换静音"></button>
</div>
