/* Live Photo 容器样式 */
.live-photo-container {
    position: relative;
    display: inline-block;
    cursor: pointer;
    overflow: hidden;
    line-height: 0; /* 消除图片下方的间隙 */
    border-radius: 8px; /* 容器圆角 */
    box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1); /* 可选：轻微阴影提升质感 */
}

/* 图片和视频层叠 */
.live-photo-static,
.live-photo-video {
    width: 100%;
    height: auto;
    transition: opacity 0.3s ease;
    border-radius: 8px; /* 统一图片和视频的圆角值 */
}

/* 视频默认隐藏 */
.live-photo-video {
    position: absolute;
    top: 0;
    left: 0;
    opacity: 0;
    object-fit: cover; /* 确保视频覆盖整个容器 */
}

/* 鼠标悬停时隐藏静态图，显示视频 */
.live-photo-container:hover .live-photo-static {
    opacity: 0;
}

.live-photo-container:hover .live-photo-video {
    opacity: 1;
}

/* Live 标志 */
.live-badge {
    position: absolute;
    top: 10px;
    right: 10px;
    z-index: 3;
    background-color: rgba(0, 0, 0, 0.6);
    color: #fff;
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 12px;
    font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, sans-serif;
    pointer-events: none; /* 确保鼠标事件能穿透标志 */
    opacity: 0.8;
    display: flex;
    align-items: center;
}

/* 红色闪烁点 */
.live-badge::before {
    content: '';
    display: inline-block;
    width: 8px;
    height: 8px;
    background-color: #ff0000;
    border-radius: 50%;
    margin-right: 6px;
    animation: blink 1.5s infinite;
}

@keyframes blink {
    0% { opacity: 1; }
    50% { opacity: 0.3; }
    100% { opacity: 1; }
}

/* 静音按钮样式 - 位置调整 */
.live-photo-mute-btn {
    position: absolute;
    bottom: 25px; /* 从10px调整为15px，向上移动5px */
    right: 10px;
    z-index: 3;
    background-color: rgba(0, 0, 0, 0.6);
    color: #fff;
    border: none;
    border-radius: 50%;
    width: 28px;
    height: 28px;
    display: flex;
    align-items: center;
    justify-content: center;
    cursor: pointer;
    font-size: 14px;
    opacity: 0.8;
    transition: opacity 0.2s ease;
}

.live-photo-mute-btn:hover {
    opacity: 1;
    background-color: rgba(0, 0, 0, 0.8);
}
