        if (now >= nextUpdateTime) {
          nextUpdateTime.setDate(nextUpdateTime.getDate() + 1);
        }

        // 计算到9点的毫秒数
        const timeUntilUpdate = nextUpdateTime - now;

        // 设置定时器
